mapper.update:
 		update go_work set GO_WORK_TIME = '', GO_WORK_OUT_TIME = '', go_work_total_time = ''
		where mng_id = #{mngId}           
		and to_char(go_work_date,'YYYY-MM-DD') = #{goWorkDate}

 		update go_work set GO_WORK_REASON = #{goWorkReason}
		where mng_id = #{mngId}           
		and to_char(go_work_date,'YYYY-MM-DD') = #{goWorkDate}
