mapper.select:
SELECT MENU_ID as menuId, 
	  MENU_NAME as menuName
	FROM MENU
	WHERE MENU_ID = #{menuId}
	 OR MENU_UPCODE = #{menuId}
	 ORDER BY MENU_ID

update.foreach:
        	UPDATE MENU 
			   SET MENU_CATEGORY = #{item.category}
	         
         	    MENU_ID =  #{item.menuId}
 			 OR MENU_UPCODE =  #{item.menuId}

UPDATE MENU 
   SET MENU_NAME = #{item.menuName}
 WHERE MENU_ID = #{item.menuId}

