mapper.select:
SELECT D.STUDENT_NAME ,B.LESSON_NAME ,A.PAYMENT_DATE ,A.PAYMENT_PRICE ,B.LESSON_PRICE ,A.PAYMENT_MONTHS FROM PAYMENT A,LESSON B, LESSONASSIGNMENT C, STUDENT D WHERE A.LESSONASSIGNMENT_ID = C.LESSONASSIGNMENT_ID AND B.LESSON_ID = C.LESSON_ID AND A.PARENT_ID = D.PARENT_ID AND A.STUDENT_ID = D.STUDENT_ID AND A.PARENT_ID = #{parentId}
AND D.STUDENT_ID = #{studentId}
ORDER BY PAYMENT_DATE DESC

SELECT D.STUDENT_NAME ,B.LESSON_NAME ,A.PAYMENT_DATE ,A.PAYMENT_DUEDATE ,B.LESSON_PRICE ,A.PAYMENT_MONTHS ,A.STUDENT_ID ,C.LESSONASSIGNMENT_ID FROM PAYMENT A,LESSON B, LESSONASSIGNMENT C, STUDENT D WHERE A.LESSONASSIGNMENT_ID = C.LESSONASSIGNMENT_ID AND B.LESSON_ID = C.LESSON_ID AND A.PARENT_ID = D.PARENT_ID AND A.STUDENT_ID = D.STUDENT_ID AND A.PARENT_ID = #{parentId}
AND D.STUDENT_ID = #{studentId}
AND A.PAYMENT_ID IN (SELECT PAYMENT_ID FROM (SELECT PAYMENT_ID ,LESSONASSIGNMENT_ID ,STUDENT_ID ,PAYMENT_DATE ,DENSE_RANK() OVER (PARTITION BY STUDENT_ID,LESSONASSIGNMENT_ID ORDER BY PAYMENT_DATE DESC) AS RANK FROM PAYMENT ORDER BY PAYMENT_DATE DESC) WHERE RANK = 1)
UNION
SELECT D.STUDENT_NAME ,B.LESSON_NAME ,NULL AS PAYMENT_DATE ,NULL AS PAYMENT_DUEDATE ,B.LESSON_PRICE ,NULL AS PAYMENT_MONTHS ,D.STUDENT_ID ,C.LESSONASSIGNMENT_ID FROM PARENT A, LESSON B, LESSONASSIGNMENT C, STUDENT D, TAKE_LESSON E WHERE B.LESSON_ID = C.LESSON_ID AND A.PARENT_ID = D.PARENT_ID AND C.LESSONASSIGNMENT_ID = E.LESSONASSIGNMENT_ID AND D.STUDENT_ID = E.STUDENT_ID
AND D.STUDENT_ID = #{studentId}
AND (D.STUDENT_ID,C.LESSONASSIGNMENT_ID) IN (SELECT D.STUDENT_ID ,E.LESSONASSIGNMENT_ID FROM STUDENT D, TAKE_LESSON E WHERE D.STUDENT_ID = E.STUDENT_ID AND D.PARENT_ID = #{parentId} MINUS SELECT STUDENT_ID ,LESSONASSIGNMENT_ID FROM (SELECT PAYMENT_ID ,LESSONASSIGNMENT_ID ,STUDENT_ID ,PAYMENT_DATE ,DENSE_RANK() OVER (PARTITION BY STUDENT_ID,LESSONASSIGNMENT_ID ORDER BY PAYMENT_DATE DESC) AS RANK FROM PAYMENT ORDER BY PAYMENT_DATE DESC) WHERE RANK = 1)
