mapper.insert:
insert into KAKAO_SEND
(KAKAO_SEND_ID
,KAKAO_RECEIVER_NAME ,KAKAO_SEND_CONTENT
,KAKAO_SEND_DATE ,MNG_ID
,KAKAO_SEND_TITLE ,KAKAO_SEND_TYPE
,KAKAO_RECEIVER_HP ,KAKAO_RECEIVER_EMAIL
,KAKAO_RECEIVER_TYPE)
select KAKAO_SEND_SEQ.nextval, A.* from(
	
		 select #{item.kakaoReceiverName},#{kakaoSendContent}
		 ,sysdate,#{mngId}
		 ,#{kakaoSendTitle},#{kakaoSendType}
		 ,#{item.kakaoReceiverHp},#{item.kakaoReceiverEmail}
		 ,#{item.kakaoReceiverType}
		 from dual
	
)A

mapper.select:
select count(*) from (select KAKAO_SEND_ID as kakaoSendId ,KAKAO_RECEIVER_NAME as kakaoReceiverName ,KAKAO_SEND_CONTENT as kakaoSendContent ,KAKAO_SEND_DATE as kakaoSendDate ,MNG_ID as mngId ,KAKAO_SEND_TITLE as kakaoSendTitle ,KAKAO_SEND_TYPE as kakaoSendType ,KAKAO_RECEIVER_HP as kakaoReceiverHp ,KAKAO_RECEIVER_EMAIL as kakaoReceiverEmail ,KAKAO_RECEIVER_TYPE as kakaoReceiverType from KAKAO_SEND where KAKAO_SEND_ID is not null
and ( KAKAO_RECEIVER_NAME like '%'||#{keyword}||'%' or KAKAO_SEND_CONTENT like '%'||#{keyword}||'%' or KAKAO_SEND_DATE like '%'||#{keyword}||'%' or MNG_ID like '%'||#{keyword}||'%' or KAKAO_SEND_TITLE like '%'||#{keyword}||'%' or KAKAO_SEND_TYPE like '%'||#{keyword}||'%' or KAKAO_RECEIVER_HP like '%'||#{keyword}||'%' or KAKAO_RECEIVER_TYPE like '%'||#{keyword}||'%')
)
