mapper.update:
UPDATE PARENT
SET PARENT_PW = #{emailAddress}
	WHERE PARENT_ID = #{emailAddress}

UPDATE PARENT
SET PARENT_PW = #{pw}
	WHERE PARENT_ID = #{id}

mapper.select:
select count(*) from (select a.parent_id as id ,a.PARENT_ALARM_METHOD_CODE as parentAlarmMethodCode ,a.PARENT_NAME as parentName ,a.PARENT_EMAIL as parentEmail ,a.PARENT_ALARM_YN as parentAlarmYn ,a.PARENT_HP as parentHp ,a.PARENT_ALARM_GO as parentAlarmGo ,a.PARENT_ALARM_OUT as parentAlarmOut ,a.PARENT_DIVISION_CODE as parentDivisionCode ,b.student_name as childrenName ,e.school_name as childrenSchoolName ,f.SCODE_VALUE as childrenGradeCode ,g.SCODE_VALUE as childrenGrade ,d.class_name as childrenClassName from parent a, student b, belong c, class d, school e, STANDARDCODE f, STANDARDCODE g where a.parent_id = b.parent_id and b.student_id = c.student_id and c.class_id = d.class_id and b.school_No = e.school_id and b.STUDENT_GRADE_CATEGORY_CODE = f.SCODE_KEY and b.STUDENT_GRADE = g.SCODE_KEY
and ( a.parent_id like '%'||#{keyword}||'%' or a.PARENT_ALARM_METHOD_CODE like '%'||#{keyword}||'%' or a.PARENT_NAME like '%'||#{keyword}||'%' or a.PARENT_EMAIL like '%'||#{keyword}||'%' or a.PARENT_ALARM_YN like '%'||#{keyword}||'%' or a.PARENT_HP like '%'||#{keyword}||'%' or a.PARENT_ALARM_GO like '%'||#{keyword}||'%' or a.PARENT_ALARM_OUT like '%'||#{keyword}||'%' or a.PARENT_DIVISION_CODE like '%'||#{keyword}||'%' or b.student_name like '%'||#{keyword}||'%' or e.school_name like '%'||#{keyword}||'%' or f.SCODE_VALUE like '%'||#{keyword}||'%' or g.SCODE_VALUE like '%'||#{keyword}||'%' or d.class_name like '%'||#{keyword}||'%')
)
